import React, { useState } from 'react';
import './Game.css';





function Game(){
    function urlLink(){
        window.open('https://store.steampowered.com/app/1174180/','_blank');
    }

var tabs = document.querySelectorAll(".tabs_wrap ul li");

function tabsActive(){
    tabs.forEach((tab)=>{
        tab.classList.remove("active");
    })
}

tabs.forEach((tab)=>{
  tab.addEventListener("click", ()=>{
    tabs.forEach((tab)=>{
      tab.classList.remove("active");
    })
    tab.classList.add("active");
})
})
    return(
        <div className="list-wrapper">
          <div className="tabs_wrap">
            <ul>
            <li data-tabs="male" onClick={tabsActive}>Male</li>
            <li data-tabs="female">Female</li>
            <li data-tabs="female">Female</li>
            <li data-tabs="female">Female</li>
            <li data-tabs="both" className="active">Both</li>
            </ul>
        </div>
        <ul className="list">

        <li className="list-item" onClick={urlLink}>
            <div className="list-item__image">
                <img src="https://cdn.akamai.steamstatic.com/steam/apps/1174180/header.jpg?t=1656615305" alt="Thumbnail" />
            </div>
            <div className="list-item__content">
            <div className='price'><p>3300000</p></div>
                <h4>Red Dead Redemption 2 </h4>

                <p> 올해의 게임 175여 개를 수상하고 250개 이상의 완벽한 평가를 받은 Red Dead Redemption 2는 현대 시대가 시작될 무렵 무법자인 아서 모건과 악명 높은 반 더 린드 갱단이 미국 전역을 따라 도주하는 장대한 서사시입니다. 모두가 함께 즐길 수 있는 생생한 세계인 Red Dead 온라인 역시 포함됩니다. </p>
            </div>

        </li>


        <li className="list-item">
            <div className="list-item__image" >
                <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/210284/59.jpg" alt="Thumbnail" />
            </div>
            <div className="list-item__content">
                <h4> Dean Sullivan </h4>
                <p> 6727 Airplane Ave </p>
            </div>
        </li>

        <li className="list-item">
            <div className="list-item__image">
                <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/210284/78.jpg" alt="Thumbnail" />
            </div>
            <div className="list-item__content">
                <h4> Elizabeth Austin </h4>
                <p> 6685 Blossom Hill Rd </p>
            </div>
        </li>

    </ul>
    </div>


        );
    }



        export default Game;
